mapper.update:
update dish
category_id=#{categoryId}, name=#{name}, description=#{description}, update_user=#{updateUser}, update_time=#{updateTime}, image=#{image}, price=#{price}, status=#{status}
where id=#{id}

mapper.select:
select dish.*,category.name  category_name from dish,category

    category.id=category_id
    and name like concat('%',#{name},'%')
    and category_id=#{categoryId}
    and status=#{status}

order by update_time desc
